import React, { useState, useEffect  } from 'react'
import axios from 'axios'
import './App.css'
import riseupp from './img/ico.webp'

const API_KEY = 'q4EQa1ke8pgUUkBoc5r2pGnVLdBMFhMMzLJ09kKHkU6FZxL3JzuwgS7S'
const BASE_URL = 'https://api.pexels.com/v1'
const IMG_PER_PAGE =6

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);
  const [hoveredImage, setHoveredImage] = useState(null);

  const handleSearch = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`${BASE_URL}/search`, {
        headers: {
          Authorization: API_KEY,
        },
        params: {
          query: searchQuery,
          per_page: IMG_PER_PAGE,
          page: currentPage,
        },
      });
      setImages(response.data.photos);
      setTotalPages(response.data.total_results / IMG_PER_PAGE);
    } catch (error) {
      console.error('Error fetching images:', error);
      setImages([]);
      setTotalPages(0);
    } finally {
      setLoading(false);
    }
  };


  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const handlePrevPage = () => {
    if (currentPage > 1) {
      setCurrentPage((prevPage) => prevPage - 1);
    }
  };

  const handleNextPage = () => {
    if (currentPage < totalPages) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  const handleImageMouseEnter = (image) => {
    setHoveredImage(image);
  };

  const handleImageMouseLeave = () => {
    setHoveredImage(null);
  };


  return (
    <div>

      <div className="ico">
   <img src={riseupp} alt="riseupp" />
    </div>
    <div className="search-bar">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Enter your search query"
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <>
          {images.length > 0 ? (
            <div className="image-list">
              {images.map((image) => (
                <div
                  key={image.id}
                  className="image-container"
                  onMouseEnter={() => handleImageMouseEnter(image)}
                  onMouseLeave={handleImageMouseLeave}
                >
                  <a href={image.src.original} target="_blank" rel="noopener noreferrer">
                    <img src={image.src.medium} alt={image.photographer} />
                  </a>
                  {hoveredImage && hoveredImage.id === image.id && (
                    <div className="image-tooltip">
                      <h3>{hoveredImage.photographer}</h3>
                    </div>
                  )}
                </div>
              ))}
            </div>
          ) : (
            <div className="no-results">No results found.</div>
          )}

          {totalPages > 1 && (
            <div className="pagination">
              <button onClick={handlePrevPage} className="arrow">
                &larr;
              </button>
              {Array.from({ length: totalPages }, (_, index) => index + 1).map((page) => (
                <button
                  key={page}
                  onClick={() => handlePageChange(page)}
                  className={page === currentPage ? 'active' : ''}
                >
                  {page}
                </button>
              ))}
              <button onClick={handleNextPage} className="arrow">
                &rarr;
              </button>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default App;
