import React, { useState } from 'react'
import axios from 'axios'
import './App.css'
import riseupp from './img/ico.webp'

const API_KEY = 'a--kn0b1xNLH9vXRLb9jAqVy3bur2ALNib2SsG-2iN0'
const BASE_URL = 'https://api.unsplash.com'
const IMG_PER_PAGE = 10

const fetchImages = async (query) => {
  try {
    const response = await axios.get(`${BASE_URL}/search/photos`, {
      headers: {
        Authorization: `Client-ID ${API_KEY}`,
      },
      params: {
        query: query,
               },
    })

    return response.data.results
  } catch (error) {
    console.error('Error fetching images:', error)
    return []
  }
}

const App = () => {
  const [search, setsearch] = useState('')
  const [images, setImages] = useState([])
  const [showTooltip, setShowTooltip] = useState(false)
  const [tooltipContent, setTooltipContent] = useState('')

  const handleSearch = async () => {
    const fetchedImages = await fetchImages(search)
    setImages(fetchedImages)

  }

  const handleImageHover = (image) => {
    setTooltipContent(image.description)
    setShowTooltip(true)
  }

  const handleImageLeave = () => {
    setShowTooltip(false)
  }

  return (
   <>
   <div className="ico">
   <img src={riseupp} alt="riseupp" />
    </div>

    <div>
      <div className="search-bar">
        <input
          type="text"
          value={search}
          onChange={(e) => setsearch(e.target.value)}
          placeholder="Search images"
        />
        <button onClick={handleSearch}>Search</button>
      </div>


        <div className="image-list">
          {images.map((image) => (
            <div
              key={image.id}
              className="image-container"
              onMouseEnter={() => handleImageHover(image)}
              onMouseLeave={handleImageLeave}
            >
              <a
                href={image.urls.full}
                target="_blank"
                rel="img"
                title={image.description}
              >
                <img src={image.urls.small} alt={image.description} />
              </a>
            </div>
          ))}
        </div>


      {showTooltip && <div className="tooltip">{tooltipContent}</div>}
    </div>
    </>
  )
}

export default App
