import React, { useState, useEffect  } from 'react'
import axios from 'axios'
import './App.css'
import riseupp from './img/ico.webp'

const API_KEY = 'a--kn0b1xNLH9vXRLb9jAqVy3bur2ALNib2SsG-2iN0'
const BASE_URL = 'https://api.unsplash.com'
const IMG_PER_PAGE = 5

const fetchImages = async (query, page) => {
  try {
    const response = await axios.get(`${BASE_URL}/search/photos`, {
      headers: {
        Authorization: `Client-ID ${API_KEY}`,
      },
      params: {
        query: query,
        per_page:IMG_PER_PAGE,
        page:page 
          },
    })

    return response.data.results
  } catch (error) {
    console.error('Error fetching images:', error)
    return {results: [], total_pages: 0}
  }
}


const App = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(0);

  const handleSearch = async () => {
    setCurrentPage(1);
    setLoading(true);
    const { results, total_pages } = await fetchImages(searchQuery, 1);
    setImages(results);
    setTotalPages(total_pages);
    setLoading(false);
  };

  const handlePageChange = async (page) => {
    setCurrentPage(page);
    setLoading(true);
    const { results } = await fetchImages(searchQuery, page);
    setImages(results);
    setLoading(false);
  };

  useEffect(() => {
    if (searchQuery) {
      handleSearch();
    } else {
      setImages([]);
    }
  }, [searchQuery]);

  useEffect(() => {
    console.log(images); // Debugging statement to check the value of 'images' state
  }, [images]);


  return (
    <div>

      <div className="ico">
   <img src={riseupp} alt="riseupp" />
    </div>
      <div className="search-bar">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Enter your search query"
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <>
          {images && images.length > 0 ? (
            <div className="image-list">
              {images.map((image) => (
                <div key={image.id} className="image-container">
                  <a
                    href={image.urls.full}
                    target="_blank"
                    rel="noopener noreferrer"
                    title={image.description}
                  >
                    <img src={image.urls.small} alt={image.description} />
                  </a>
                </div>
              ))}
            </div>
          ) : (
            <div className="no-results">No results found.</div>
          )}
        </>
      )}

      {totalPages > 1 && (
        <div className="pagination">
          {Array.from({ length: totalPages }, (_, index) => index + 1).map((page) => (
            <button
              key={page}
              onClick={() => handlePageChange(page)}
              className={page === currentPage ? 'active' : ''}
            >
              {page}
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
